def check_correct(A, answer):
    origCopy = A[:]
    chosenPrizes = choose_prizes(A)
    indices = [0 for _ in range(len(A))]
    for i in chosenPrizes:
        indices[i] = 1
    for i in range(1, len(A)):
        if (indices[i - 1] == 1) and (indices[i] == 1):
            return False
    totalSum = 0
    for i in chosenPrizes:
        totalSum += origCopy[i]
    return totalSum == answer

class TestPrizeSelection(unittest.TestCase):
    def test_01(self):
        self.assertTrue(check_correct(tests[0], test_answers[0]))

    def test_02(self):
        self.assertTrue(check_correct(tests[1], test_answers[1]))

    def test_03(self):
        self.assertTrue(check_correct(tests[2], test_answers[2]))

    def test_04(self):
        self.assertTrue(check_correct(tests[3], test_answers[3]))
       
    def test_05(self):
        self.assertTrue(check_correct(tests[4], test_answers[4]))

